-- ----------------------------
-- Table structure for cars
-- ----------------------------
DROP TABLE IF EXISTS `cars`;
CREATE TABLE `cars` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `product_id` int(11) NOT NULL,
  `wheelbase` varchar(100) DEFAULT NULL COMMENT '轴距',
  `length` varchar(100) DEFAULT NULL COMMENT '车身长度',
  `width` varchar(100) DEFAULT NULL COMMENT '车身宽度',
  `height` varchar(100) DEFAULT NULL COMMENT '车身高度',
  `frontgauge` varchar(100) DEFAULT NULL COMMENT '前轮距',
  `trackrear` varchar(100) DEFAULT NULL COMMENT '后轮距',
  `weight` varchar(100) DEFAULT NULL COMMENT '整车质量',
  `maxspeed` varchar(100) DEFAULT NULL COMMENT '最高时速',
  `enginetype` varchar(100) DEFAULT NULL COMMENT '发动机型号',
  `displacement` varchar(100) DEFAULT NULL COMMENT '排量',
  `maxsoup` varchar(100) DEFAULT NULL COMMENT '最大马力',
  `maxpower` varchar(100) DEFAULT NULL COMMENT '最大功率',
  `torque` varchar(100) DEFAULT NULL COMMENT '扭矩',
  `torquerev` varchar(100) DEFAULT NULL COMMENT '最大扭矩转速',
  `shifttype` varchar(100) DEFAULT NULL COMMENT '换挡方式',
  `gear` varchar(100) DEFAULT NULL COMMENT '档位',
  `created_at` timestamp NULL DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`),
  KEY `productid` (`product_id`) USING BTREE
) ENGINE=MyISAM AUTO_INCREMENT=10 DEFAULT CHARSET=utf8;

