-- ----------------------------
-- Table structure for case
-- ----------------------------
DROP TABLE IF EXISTS `case`;
CREATE TABLE `case` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `content` text NOT NULL COMMENT '正文',
  `title` varchar(200) NOT NULL COMMENT '标题',
  `type_id` int(11) NOT NULL,
  `writer` varchar(100) DEFAULT NULL COMMENT '作者',
  `source` varchar(400) DEFAULT NULL COMMENT '新闻来源',
  `hints` int(11) DEFAULT '0' COMMENT '点击量',
  `checkup` int(11) DEFAULT '0',
  `imagepath` varchar(100) DEFAULT NULL COMMENT '图片路径',
  `created_at` timestamp NULL DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`),
  KEY `typeId` (`type_id`)
) ENGINE=MyISAM AUTO_INCREMENT=11 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of case
-- ----------------------------
INSERT INTO `case` VALUES ('1', '2013年4月20日，四川省雅安市芦山县发生7.0级地震，地震牵动着全省、全国人民的心，“积极行动，抗震救灾”成为了每个人共同的愿望和无声的命令。省政府物流办在省委省政府的统一领导和部署下也积极行动起来，迅速投入抗震救灾工作之中\r\n$$省物流办主任联系成都、攀枝花、乐山等地物流主管部门，要求其做好运力保障和物资准备，听从当地政府统一指挥调度。\r\n$$地震无情人有情，霸龙物流数名司机朋友从雅安地震发生后一直在成都市政府部门安排下免费运送救灾物资，运送物资包括药品、挂面、帐篷、日用品等，他们连续几日在灾区和救灾物资集结点之间往返。\r\n$$截止4月21日18时，在省物流办的统一协调下，全省共有13家物流企业出动60余车次参与应急保障;100余吨物资已运抵一线。其中成都霸龙物流有限公司在此次运输活动中表现突出，当有关领导表扬他们为保障灾区人民的基本生活而劳苦奔波时，他们说：其实最应该感谢的是公司最近刚从宏达公司购进的两辆柳汽景逸，它们在此次路途中的表现相当给力！', '成都霸龙物流有限公司 ', '2', '宏伟动力', '宏伟动力', '9', '0', '2016-12-20/5858edae45db7.jpg', '2016-12-16 16:41:04');
INSERT INTO `case` VALUES ('2', '\r\n	成都志强钢化玻璃有限公司位于成都市温江区旺海路12号，公司自创立之日起，一直致力于玻璃深加工产品的开发和技术的应用，凭借“立足市场，开拓发展，用户至上的原则和开放的经营理念、先进的企业管理模式、锲而不舍的创新精神，得到了新老客户的一致好评。因业务发展需要，此公司于2015年9月通过我处订购一台东风柳汽， 车辆买进第三天就开始投入营运， 当这批装载着十五吨成品的车辆经过700多公里，八个多小时的行程，平安准确地到达西安客户的手中时，客户高兴地说：我以为到半夜这批货才能到，那样的话，卸车时就得操不少心了。$$企业选择何种运输工具会影响到产品定价、准时交货和物品到达目的地时的情况，所有的这些都关系到客户的满意程度。所以，企业用户在选择运输车辆时一定要选一辆质量优越，安全可靠的车。\r\n', '成都志强钢化玻璃有限公司', '2', '宏伟动力', '宏伟动力', '352', '0', '2016-12-20/5858a3973b212.png', '2016-12-16 16:46:43');
INSERT INTO `case` VALUES ('3', '贵州货车帮供应链管理有限公司成立于2015年12月，公司坐落在贵州省贵阳市经济技术开发区开发大道123号。自公司成立之后，业务范围一直在更改，但有三方面一直保持不变更，那便是：货物运输、汽车租赁和销售二手车。这些也正是当下企业捞金的热门，但该公司有自己独特的理念。$$在货物运输上，贵州货车帮供应链管理有限公司只选择东风柳汽车型，它的载货量不但能给公司节省车辆数花销，而且能够在维修费用和耗油上节省开支，这也是其公司不断发展壮大的原因之一，据该公司负责人表示，东风柳汽已经得到了包括货车司机在内等人的认可，同时在二手车回收时的维修，大多都会选择东风柳汽的配件，公司省心，客户放心！东风柳汽于贵州货车帮供应链管理有限公司而言，已经成了其公司的聚宝盆。', '贵州货车帮供应链管理有限公司', '5', '宏伟动力', '宏伟动力', '9', '0', '2016-12-20/5858f02b12f64.jpg', '2016-12-20 11:05:03');
INSERT INTO `case` VALUES ('6', '\r\n	四川金桥物流有限公司成立于1993年，是一家集公路运输、仓储服务、配送服务等业务为一体的综合性物流企业集团。获得国家4A物流企业资质和四川省名牌企业称号。公司目前拥有？十辆专业配送运输车辆能够立即响应并且实时配送。\r\n\r\n\r\n	$$其中，在我公司柳汽乘龙2015年的推介会上，四川金桥物流有限公司一次性成功订车成功六台。这六台车主要应用在四川、重庆、云南、湖北、甘肃、广东、江苏等省份二三级城市的专线快运中，在长途运输上，显现出柳汽乘龙得天独厚的优势，这是其他车型无可媲美的。$$金桥物流总经理刘显付对乘龙汽车的卓越品质赞不绝口，目前金桥物流已经与我公司达成战略合作关系，表示以后需要购进新车辆一定首选我们宏达公司。这是对公司车辆质量的认可，也是互利共赢的成功事例。\r\n', '四川金桥物流有限公司', '1', '宏伟动力', '宏伟动力', '2', '0', '2016-12-20/5858f1806739d.jpg', '2016-12-20 16:53:20');
INSERT INTO `case` VALUES ('4', '\r\n	李先生，长期做运输生意，自己成立了一个小型运输公司。在前几年，生意红火，但是盈利额度不高，总的是由于运输物流过程中造成的损失，这让李先生很犯难。雇员们普遍反映的问题是，货运车的载货量不够多，导致需要多次往返，花费太多的人力物力，这让李先生也无可奈何，只能不断地寻找更好的车型。\r\n\r\n\r\n	$$直到两年前的一次同行的小型聚会上，偶然听同行们对陕汽的载货潍柴270马力车型赞不绝口，自己也抱着试试看的心态去试车了，发现不管实在爬坡时的抓地力还是在制动、载货量上，都是为自己的公司量身定做啊。于是李先生购买了5俩该型载货车，这几年的运营利润告诉他，他的选择是正确的！迄今为止，他都在使用该款车型，而且不仅是他的公司在使用，同时他也推荐给自己认识的朋友同行们，他说“好的东西，大家应该要共享。”\r\n', '陕汽载货车潍柴270马力', '3', '宏伟动力', '宏伟动力', '2', '0', '2016-12-20/5858e23c96ff9.jpg', '2016-12-20 15:48:12');
INSERT INTO `case` VALUES ('5', '&nbsp;陈先生是土生土长的冠县人，受周围朋友的影响，他很早就加入了浩浩荡荡的煤炭运输大军中。自己跑运输在当地是一个致富的渠道，地处沿海省份的山东省工业发达，用煤量大，而煤炭产地主要集中在中国中部的晋陕蒙等地区。生产与消费的不平衡，决定了“西煤东运”、“北煤南运”的运输格局。其中，山西的煤炭大多通过重卡运往河北及山东的沿海地区，然后再通过运煤的货轮，运往其他地区。陈先生就是这运输大潮中的一员。\r\n	$$依陈先生所诉，虽然说煤炭运输讲究效率，对油耗并不是很看重，但油耗当然是越低越好。陈先生说，336马力的新M3000重卡，百公里油耗约为33L，在煤炭运输行业中几乎是最低，他非常满意。正是因为陕汽新M3000如此给力，陈先生每逢同行需要换车时，便向同行推荐它，这是对陕汽M3000的赞许和肯定。', '陕汽新M3000 SX12064k549', '3', '宏伟动力', '宏伟动力', '0', '0', '2016-12-20/5858e4c4ec9bf.png', '2016-12-20 15:59:01');
INSERT INTO `case` VALUES ('7', '\r\n	东风天龙旗舰系列车型一在市场上亮相便受到了广大爱车人士的普遍关注，主要是因为天龙旗舰系列都搭载了康明斯ISZ13L发动机。ISZ发动机有着领先行业的13升排量，是真正的13L发动机（12.975L），马力可达560hp，超乎想象的动力性和经济性，适合新一代高效运输需求；可在低转速时实现大扭矩，1000rpm即可实现最大扭矩达2300N.m，同类产品中同马力段扭矩最大；同时经济油耗区域也非常宽广，经济性上较同类发动机有极大的优势。\r\n\r\n\r\n	$$据使用客户反馈调查数据显示，搭配了该发动机的车型，能够适应多种地形，爬坡不费劲而且驾驶舒适度高。动力强，油耗低，似乎已经成了该款车型与其他车型竞争时最大的优势，该车型在客户圈里的口誉也是极好的。在交通事故中需要更换发动机的抢修，常常听到客户要求维修公司更换康明斯发动机，这很好的显现了康明斯发动机质量好得深入人心。\r\n', '康明斯ISZ13L发动机', '6', '宏伟动力', '宏伟动力', '0', '0', '2016-12-20/5858fa877e2d6.jpg', '2016-12-20 17:31:51');
INSERT INTO `case` VALUES ('8', '\r\n	在2016年6月里，重庆强顺物流成交了25辆东风载货车，这些车型统一配置了康明斯B系列发动机。市场上普见的汽车故障为缸体、缸盖的设计不够严谨，容易造成漏水漏油的现象，这是故障率较高的原因。为了符合客户需求和实际应用的要求，同时满足设计合理化，经过康明斯发动机研究人员的共同探讨、合作，康明斯B系列应运而生。\r\n\r\n\r\n	$$据该物流公司负责人所述，吸引他们公司的关注点是在这些车型中配置的发动机。在该款发动机中，集成化设计的缸体和缸盖，废气旁通阀的设计使低俗性能更加完美，汽车动力性更好，优异的冷启动性能是它在各类发动机中脱颖而出。最关心的油耗也大大低于同类产品，同时满足2欧排放标准。据大数据调查，有87%的客户优先选择配有康明斯发动机的车型，这似乎成为了好车标配的认可。\r\n', '康明斯B系列发动机', '6', '宏伟动力', '宏伟动力', '0', '0', '2016-12-20/5858fc59676cd.jpg', '2016-12-20 17:39:37');
INSERT INTO `case` VALUES ('9', '&nbsp;在2016年5月15日13时，在上海一路段上，有一辆公交车着火，消防队员迅速赶赴现场，疏散了乘客与围观群众，经过媒体的曝光，发动机事故引起了人们的高度关注，人们对发动机造成的隐患也是特别担心。在公交车客运车发动机的选择上也让客运中心负责人苦恼不已，对发动机的选择无可下手。无可奈何的客运负责人选择广为纳谏，听从客运司机朋友对多款发动机的使用评价，最后选择了客车统一配置玉柴发动机的决定。\r\n	$$&nbsp;根据全国消防队的统计，每年因发动机造成的交通事故不在少数，其中每五十起事故中的车辆只有一个车上配有玉柴发动机，侧面反映出玉柴发动机设计的合理性以及安全性。同时，它也侧重于汽车变速器的开发，致力于做成一个发动机的标准旗杆，事实上，在客户与汽车采购经理的眼中，它已经成为了优选，没有之一。在每年的大数据显示表示，玉柴发动机的年销量不断上涨，这说明玉柴发动机在人们的心中受到越来越重的认同度。', '玉柴发动机', '4', '宏伟动力', '宏伟动力', '0', '0', '2016-12-20/5858fdb1eb9ad.jpg', '2016-12-20 17:42:32');
INSERT INTO `case` VALUES ('10', '\r\n	2016年6月18日开始，四川泸州市古蔺县遭受暴雨袭击，最大降雨量达到了153.8毫米。强降雨受到省政府的高度关注，相关部门部署了人员撤离转移计划，联系了省物流办对处于危险区的92户361人快速撤离、成功避险。同时，广大市民自发组成救援车队，配合政府的指令参与抢险救援工作。$$在此次抢险过程中，有7辆车因发动机出现了故障，在更换发动机时，因为维修公司发动机剩货不足，只剩下了玉柴发动机，于是全部故障车更换了玉柴发动机，在长达21天的救灾日子中，该7辆车并没有再出现因发动机而出现的故障。这次的救援工作受到了社会和政府的高度评价，玉柴发动机的优良性能也在此次抢险中得到强有力的彰显，在广大司机朋友中广为盛誉。也是经过了这次事件，玉柴发动机得到了一次免费的广告，让人民记住了“玉柴”二字。\r\n', '玉柴发动机', '1', '宏伟动力', '宏伟动力', '5', '0', '2016-12-20/5858fd6d4ea5f.jpg', '2016-12-20 17:44:13');

