DROP TABLE IF EXISTS `newstype`;
CREATE TABLE `newstype` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(32) NOT NULL,
  `created_at` timestamp NULL DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of newstype
-- ----------------------------
INSERT INTO `newstype` VALUES ('1', '公司动态', null);
INSERT INTO `newstype` VALUES ('2', '行业新闻', null);

-- ----------------------------
-- Table structure for product
-- ----------------------------
DROP TABLE IF EXISTS `product`;
CREATE TABLE `product` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(200) NOT NULL COMMENT '产品标题',
  `recommend` text NOT NULL COMMENT '文字介绍',
  `type_id` int(11) NOT NULL,
  `hints` int(11) DEFAULT '0' COMMENT '点击量',
  `plist` varchar(100) DEFAULT NULL COMMENT '产品列表',
  `abstract` varchar(100) DEFAULT NULL COMMENT '产品简介图片',
  `imagepath` varchar(100) DEFAULT NULL COMMENT '产品图片',
  `parameter` varchar(100) DEFAULT NULL COMMENT '参数配置',
  `created_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`),
  KEY `typeId` (`type_id`)
) ENGINE=MyISAM AUTO_INCREMENT=13 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of product
-- ----------------------------
INSERT INTO `product` VALUES ('1', 'SY385H大型挖掘机', '\r\n	矿山型专用大型挖掘机，配备五十铃四冲程六缸涡轮增压中冷直喷发动机，动力十足，适用于重载、矿山各种复杂的工况，马力强劲，着力于碎石、挖土各项难题的解决。集节能、环保、高效于一身，汇低能耗、高性能、易保养于一体。整体性能稳定，两年零故障，质量有保证。排量扭矩比比同行业品牌高20个百分点，油耗与同行业品牌相比低10%，配备专属发动机、更新液压系统并采用独创的智能控制系统，更有效的利用了发动机的输出功率，作业效率提高6%，加强型工作装置和下车架使得动力更强、稳定性更高，稳定性能比同行业品牌提高8%。\r\n', '1', '0', '2016-12-12/584e49bca94fc.jpg', '2016-12-12/584e49bcb390c.png', '2016-12-12/584e49bcb507c.JPG$$2016-12-12/584e49bcb67ec.JPG$$2016-12-12/584e49bcb778c.JPG', '2016-12-12/584e49bcb44c4.png', '2016-12-12 14:54:52');
INSERT INTO `product` VALUES ('2', 'SSR200-3单钢轮压路机', '\r\n	\r\n\r\n\r\n	高性价比全液压产品，20000KG自重、搭载140KW大功率发动机和加强型振动泵和大排量振动马达，爬坡能力强，振动力大，使压实效率提升16.7%。智能SYMC控制系统，故障自动诊断，大屏幕液晶显示，一杆式智能作业。\r\n\r\n\r\n	\r\n', '1', '0', '2016-12-12/584e4f48d65b0.png', '2016-12-12/584e4f48d84f0.png', '2016-12-12/584e4f48d9878.jpg$$2016-12-12/584e4f48da430.jpg$$2016-12-12/584e4f48db3d0.jpg', '2016-12-12/584e4f48d8cc0.png', '2016-12-12 15:18:32');
INSERT INTO `product` VALUES ('3', 'SYL956H5轮式装载机', '\r\n	配置德纳转轴式变速箱，矿山重载型驱动桥，双管路全液压制动，安全可靠。敏感的液压系统，尖端的转向系统，实现了平稳转向和能效提高；重载型车身结构，强劲的工作装置，搭配多种作业属具，以超强的实力来适应各种恶劣工况，整机性能稳定可靠。\r\n', '1', '0', '2016-12-12/584e5778412f8.jpg', '2016-12-12/584e5778445c0.png', '2016-12-12/584e577845d30.JPG$$2016-12-12/584e5778474a0.png$$2016-12-12/584e577847c70.jpg', '2016-12-12/584e577845178.png', '2016-12-12 15:53:28');
INSERT INTO `product` VALUES ('4', ' 风行SX6 2016款 1.6L 手动标准型', '\r\n	东风风行SX6作为东风风行旗下首款SUV车型，外观设计沉稳大气，融入了众多时尚元素；在座椅布局上采用2 2 3的形式，为车内提供多种空间组合；不同车型间还配备了ESP车身稳定系统、定速巡航、胎压监测和360°全景摄像头等配置，完全满足国内消费者对一款家用SUV的全部需求。\r\n', '2', '0', '2016-12-20/5858cb53a1ef3.png', '2016-12-20/5858cb544fd8a.png', '2016-12-20/5858cb54ab6e7.png$$2016-12-20/5858cb5511009.png$$2016-12-20/5858cb55b16bd.png', null, '2016-12-20 14:10:30');
INSERT INTO `product` VALUES ('5', '福田 欧曼GTL 6系重卡 超能版 430马力 6X4牵引车(平地板)(BJ4259SNFKB)', '欧曼GTL是由福田戴姆勒汽车采用戴姆勒质量管理体系全价值链打造的中国首款欧洲标准高品质重卡，产品具有低油耗、高舒适、高安全、高可靠等特点，通过了德国TUV莱茵认证，获得“欧洲标准”认证证书，获得2012中国重卡年度车型。\r\n\r\n	\r\n', '2', '0', '2016-12-20/5858cd46d016e.png', '2016-12-20/5858cd470d321.png', '2016-12-20/5858cd4759948.png$$2016-12-20/5858cd4780997.png$$2016-12-20/5858cd47a33e5.png', null, '2016-12-20 14:18:47');
INSERT INTO `product` VALUES ('6', '欧曼ETX 6系重卡 400马力 6X4牵引车(BJ4253SNFKB-XG)', '\r\n	欧曼ETX，由福田戴姆勒汽车针对中国重卡实际使用工况，采用先进的欧洲科技为中国市场量身打造的一款高品质重卡，结合市场与用户检验，更贴心更安全。社会保有量超过50万台，在重卡之中脱颖而出。\r\n', '2', '0', '2016-12-20/5858d2098a4bc.png', '2016-12-20/5858d209de2dd.png', '2016-12-20/5858d20aa2641.png$$2016-12-20/5858d20b0f8e1.png$$2016-12-20/5858d20b9080b.png', null, '2016-12-20 14:39:07');
INSERT INTO `product` VALUES ('7', '陕汽重卡 德龙X3000 500马力6X4牵引车(SX4250XC4Q2)', '\r\n	陕汽重卡联合“黄金搭档”潍柴动力等成员，共同推出国内首款13升500-550大马力重卡，成为中国首个迈入500+平台的标志性产品，陕汽500马力产品的全新面世，无疑成为中国物流运输产品升级换代的先锋，突破传统公路物流车型格局，开创一个新的时代。\r\n', '2', '0', '2016-12-20/5858d31d52878.jpg', '2016-12-20/5858d31d81f78.png', '2016-12-20/5858d31da43cb.png$$2016-12-20/5858d31dd4232.png$$2016-12-20/5858d31e0095c.png', null, '2016-12-20 14:43:42');
INSERT INTO `product` VALUES ('8', '陕汽重卡 德龙新M3000 轻量化版 375马力 6X4牵引车(SX4256GR324)', '德龙X3000黄金版牵引车承袭陕汽48年精湛军车工艺，以“国家级企业技术中心和博士后科研工作站”强大研发能力为依托，针对高效物流运输打造的一款全新产品，行业首创500PS+，为您带来超强的动力性和极致的经济性，伴您踏上盈利新征途，全面开启中国物流省油新时代。\r\n\r\n	\r\n', '2', '0', '2016-12-20/5858d4192ddfb.jpg', '2016-12-20/5858d419511f5.png', '2016-12-20/5858d41972f84.png$$2016-12-20/5858d41996f0d.png$$2016-12-20/5858d419ce3c0.png', null, '2016-12-20 14:47:54');
INSERT INTO `product` VALUES ('9', '东风商用车 天锦中卡 180马力 4X2 6.8米排半仓栅式载货车(DFL5160CCQBX5)', '东风天锦是由东风研发人员自主研发的东风新一代中卡，以高技术含量、高可靠性、低排放及人性化设计等优势被誉为中国中卡的价值标杆，东风天锦系列车型有40多个品种，在城市和省际货物运输中成为新一代标杆。\r\n\r\n	\r\n', '2', '0', '2016-12-20/5858d66e625d7.png', '2016-12-20/5858d66e8966c.png', '2016-12-20/5858d67ce64c8.png$$2016-12-20/5858d67d3f53f.png$$2016-12-20/5858d67da01c1.png', null, '2016-12-20 14:58:05');
INSERT INTO `product` VALUES ('10', '东风商用车 新天龙重卡 启航版 420马力 6X4牵引车(DFL425AX16A)', '2006年5月18日，中国最大的汽车合资企业-- 东风汽车有限公司旗下的东风商用车公司在十堰市推出了新一代重卡平台—东风天龙。东风天龙融合了当今世界先进技术，在研发过程中实现了大量的自主创新，整车系统性能全面提升，在技术、安全性、经济性、可靠性、舒适性等方面表现突出。', '2', '0', '2016-12-20/5858d782a60bd.png', '2016-12-20/5858d782e0ab6.png', '2016-12-20/5858d78320a32.png$$2016-12-20/5858d7835aabf.png$$2016-12-20/5858d7838b3e9.png', null, '2016-12-20 15:02:27');
INSERT INTO `product` VALUES ('11', '东风柳汽 乘龙M3中卡 220马力 6X2 9.6米载货车底盘(LZ1200M3CAT)', '乘龙M3B引用欧美元素，在驾驶室平台、外观、内饰、车架和轻量化等多方面实现升级改进。驾驶室的外观设计与全新平台的H7重卡外观，形成家族式脸谱，马克· 德尚亲自操刀将高颜值低风阻完美结合。', '2', '0', '2016-12-20/5858d8b0529f0.png', '2016-12-20/5858d8b0ab344.png', '2016-12-20/5858d8b0df2ca.png$$2016-12-20/5858d8b11b8b4.png$$2016-12-20/5858d8b14d48e.png', null, '2016-12-20 15:07:29');
INSERT INTO `product` VALUES ('12', '东风柳汽 乘龙H7重卡 500马力 6X4牵引车(LZ4253H7DB)', '\r\n	由国际顶级设计团队打造，车型气势磅礴，尽显国际科技大家风范，先进的产品开发理念和全新的底盘树立了国内重卡行业新标杆。\r\n', '2', '0', '2016-12-20/5858daf095c45.png', '2016-12-20/5858daf1031eb.png', '2016-12-20/5858daf135700.png$$2016-12-20/5858daf1543fd.png$$2016-12-20/5858daf19f5a6.png', null, '2016-12-20 15:17:05');

