-- ----------------------------
-- Table structure for recruit
-- ----------------------------
DROP TABLE IF EXISTS `recruit`;
CREATE TABLE `recruit` (
  `rId` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(100) NOT NULL COMMENT '岗位名称',
  `type` varchar(100) NOT NULL COMMENT '岗位类型',
  `num` varchar(100) NOT NULL COMMENT '招聘人数',
  `place` varchar(100) NOT NULL COMMENT '地点',
  `worktime` varchar(100) NOT NULL COMMENT '工作时间',
  `demand` text NOT NULL COMMENT '要求',
  `dateandtime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`rId`)
) ENGINE=MyISAM AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;

